import "./Reviews.css";
import { useQuery } from "react-query";
import axios from "axios";

export const Reviews = ({ id }) => {
  const {data, isLoading, isError} = useQuery(["review-details", id], () => {
    return axios.get(`http://localhost:4000/reviews`);
  })

  if(isLoading) {
    return <h3>Loading...</h3>
  }

  if(isError) {
    return <h3>Error</h3>
  }

  const review = data?.data.filter( review => {
    return review.id === id;
  });
  return (
    <div className="review">
      {
        review.map(review => {
          return <div key={review.reviewId}>
            <h3>{review.name}</h3>
            <p>{review.email}</p>
            <p>{review.review}</p>
            <p>{review.rating}</p>
          </div>
        })
      }
      console.log({review})
    </div>
  )
}
