import { useState } from "react";
import "../../styles/Button.css";
import "./Cart.css";
import { useRecoilValue } from "recoil";
import { AddToCart } from "../AddToCart";

export const Cart = () => {

  const [displayCart, setDisplayCart] = useState(false); 
  const cartItems = useRecoilValue(AddToCart);

  const noOfCartItems = cartItems.length;

  return (
    <>
      <button className="cart" onClick={() => setDisplayCart(!displayCart)}>Cart ({noOfCartItems})</button>
      {
        displayCart && (noOfCartItems===0 ? <div></div> : <div>noOfCartItems</div>)
      }
    </>
  )
}
