import "../../styles/Button.css";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import { useMutation } from "react-query";
import axios from "axios";
import "./AddReview.css";

const initialValues = {
  name: "",
  email: "",
  review: "",
  rating: ""
}

const validationSchema = Yup.object({
  name: Yup.string().required("Required"),
  email: Yup.string().email("Invalid Email Format").required("Required"),
  review: Yup.string().required("Required"),
  rating: Yup.number().required("Required")
  .min(1, 'Rating must be at least 1')
  .max(5, 'Rating must be at most 5'),
});

// const handleSubmit = (values) => {
//   console.log(values);
// }


export const AddReview = ({id}) => {
  const mutation = useMutation((postData) => axios.post('http://localhost:4000/products/id', postData));

  function handleSubmit(e,values) {
    e.preventDefault();
    const formData = new FormData(e.target);
    const postData = {};
    for(const key in formData) {
        postData[`${key}`] = formData[`${key}`];
    }

    mutation.mutate(postData);
    console.log("hi" + values);
}
  return (
    <>
      <h3>Add a review</h3>
      <Formik 
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleSubmit} 
      >
          <Form>
              <div class="form">
                <label htmlFor="name">Name: {" "}</label>
                <Field type="text" id="name" name="name" placeholder="Enter your name"/>
                <ErrorMessage name="name" />
              </div>

              <div class="form">
                <label htmlFor="email">Email: {" "}</label>
                <Field type="email" id="email" name="email" placeholder="Enter your email"/>
                <ErrorMessage name="email" />
              </div>

              <div class="form">
                <label htmlFor="review">Review: {" "}</label>
                <Field as="textarea" id="review" name="review" placeholder="Enter your review"/>
                <ErrorMessage name="review" />
              </div>

              <div class="form">
                <label htmlFor="rating">Rating: {" "}</label>
                <Field type="text" id="rating" name="rating" placeholder="Enter your rating"/>
                <ErrorMessage name="rating" />
              </div>

              <div class="form">
                <button type="submit">Submit Review</button>
              </div>
          </Form>
      </Formik>
    </>
  )
}
