import "./Reviews.css";
import { useQuery } from "react-query";
import axios from "axios";
import { AddReview } from '../add-review/AddReview';

export const Reviews = ({ id }) => {
  const {data, isLoading, isError} = useQuery(["review-details", id], () => {
    return axios.get(`http://localhost:4000/reviews`);
  })

  if(isLoading) {
    return <h3>Loading...</h3>
  }

  if(isError) {
    return <h3>Error</h3>
  }

  return (
    <div className="review flex-variant reviews">
      {
        data?.data.map(review => {
          return<div key={review.reviewsId}>
            <h3>{review.name}-{review.id} - {id}-    {review.id===id}</h3>
            <p>{review.email}-{review.id === id}-</p>
            <p>{review.review}</p>
            <p>{review.rating}</p>
          </div>
        })
      }
    </div>
  )
}
