import { Link, useParams } from "react-router-dom";
import { useQuery } from "react-query";
import { ProductDetails } from "../product-details/ProductDetails";
import { ProductImg } from "../product-img/ProductImg";
import "./Product.css";
import "../../styles/ProductDetails.css";
import "../../styles/ButtonLink.css";
import axios from "axios";

export const Product = () => {
  const params = useParams();
  const id = params.id;
  const {product, isLoading, isError} = useQuery(["product-details", id], () => {
    return axios.get(`http://localhost:4000/products/${ params.id}`);
  })

  if(isLoading) {
    return <h3>Loading...</h3>
  }

  if(isError) {
    return <h3>Error</h3>
  }

  return (
    <div className="prod" key={id}> 
        <p>{id}- {product?.data.name}</p>
        <ProductImg />
        {/* <div className="prod-details">
          <ProductDetails name={product.name} description={product.description} price={product.price}/>
          <div className="product-details button-link">
            <p>In Stock</p>
            <Link to="#">Add to Cart</Link>
          </div> */}
        {/* </div> */}
    </div>
  )
}
