import { Link, useParams } from "react-router-dom";
import { useQuery } from "react-query";
import { ProductDetails } from "../product-details/ProductDetails";
import { ProductImg } from "../product-img/ProductImg";
import "./Product.css";
import "../../styles/ProductDetails.css";
import "../../styles/ButtonLink.css";
import axios from "axios";

export const Product = () => {
  const params = useParams();
  const id = params.id;
  const {data, isLoading, isError} = useQuery(["product-details", id], () => {
    return axios.get(`http://localhost:4000/products/${id}`);
  })

  if(isLoading) {
    return <h3>Loading...</h3>
  }

  if(isError) {
    return <h3>Error</h3>
  }

  return (
    <div className="prod" key={id}> 
        <ProductImg url={data?.data.variants[0].url}/>
        <div className="prod-details">
          <ProductDetails name={data?.data.name} description={data?.data.description} price={data?.data.price}/>
          <div className="product-details button-link">
            <p>Quantity: {data?.data.quantity}</p>
            <Link to="#">Add to Cart</Link>
          </div>
        </div>
    </div>
  )
}
